export default {
  name: 'TaskHome',
  data() {
    return {
      loggedIn: false,
      isExpanded: false,
      sidebarWidth: '250px',
      // Adjust as needed
      heroMarginLeft: '0',
      // Initialize hero margin
      mainContentMarginLeft: '0',
      // Initialize main content margin
      belowHeroMarginLeft: '0' // Initialize margin for elements below hero
    };
  },
  methods: {
    logout() {
      // Perform logout functionality
      this.loggedIn = false;
    },
    toggleMenu() {
      this.isExpanded = !this.isExpanded;
      localStorage.setItem("is_expanded", this.isExpanded.toString());

      // Adjust margins based on sidebar expansion
      if (this.isExpanded) {
        this.heroMarginLeft = this.sidebarWidth;
        this.mainContentMarginLeft = this.sidebarWidth;
        this.belowHeroMarginLeft = this.sidebarWidth;
      } else {
        this.heroMarginLeft = '0';
        this.mainContentMarginLeft = '0';
        this.belowHeroMarginLeft = '0';
      }
    }
  }
};
